import axios from 'axios';

const getAccessToken = () => async (dispatch) => {
  // headers
  const config = {
    headers: {
      'Content-Type': 'application/json',
    },
  };

  // body
  const body = {
    grant_type: 'client_credentials',
    client_id: 'BEECOMPANY',
    client_secret: process.env.REACT_APP_SECRET,
  };

  axios
    .post('https://identity.primaverabss.com/connect/token', config, body)
    .then((res) => res)
    .catch((err) => console.error(`Error while getting access token: ${err}`));
};

export default getAccessToken;
